<script setup lang="ts">
import {ref} from 'vue';
import axios from 'axios';

const baseApi = useRuntimeConfig().public.service_rekam_medis;
const dataGender = ref([]);
const dataBPJSAndUmum = ref<any>([]);

const genderChart = {
  chartOptions: {
    chart: {
      width: 380,
      type: 'pie',
    },
    labels: ['Laki-laki', 'Perempuan'],
    responsive: [{
      breakpoint: 480,
      options: {
        chart: {
          width: 200
        },
        legend: {
          position: 'bottom'
        }
      }
    }]
  },
}

const optionsProfileVisit = {
  annotations: {
    position: 'back'
  },
  dataLabels: {
    enabled: false
  },
  chart: {
    type: 'bar'
  },
  fill: {
    opacity: 1
  },
  plotOptions: {},
  colors: '#435ebe',
  xaxis: {
    categories: ["Jan", "Feb", "Mar", "Apr", "May", "Jun", "Jul", "Aug", "Sep", "Oct", "Nov", "Dec"],
  },
}

const fetchData = async () => {
  try {
    const {data} = await axios.get(`${baseApi}/dashboard`);
    dataGender.value = data.data.stat_gender.map((item: any) => {
      return item.total;
    });
    dataBPJSAndUmum.value = [
      {
        name: 'BPJS',
        data: data.data.stat_bpjs_permonth.bpjs
      },
      {
        name: 'Umum',
        data: data.data.stat_bpjs_permonth.umum
      }
    ];
  } catch (error) {
    console.log(error);
  }
}

onMounted(() => {
  fetchData();
});

</script>

